    private void btn0ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn0ActionPerformed
       if(txtNumber.getText().isEmpty()){
            txtNumber.setText("0");
       }else if(txtNumber.getText().contains("+")||txtNumber.getText().contains("-")||txtNumber.getText().contains("*")||txtNumber.getText().contains("/")){
           txtNumber.setText("");
           txtNumber.setText("0");
        }else{
            txtNumber.setText(txtNumber.getText()+"0");
        }
        
    }//GEN-LAST:event_btn0ActionPerformed

