    private void btnIgualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIgualActionPerformed
        num2 = Double.parseDouble(txtNumber.getText());
        if(op=="soma"){
            result = num1+num2;
        }else if(op=="sub"){
            result = num1-num2;
        }else if(op=="mult"){
            result = num1*num2;
        }else if(op=="div"){
           result = num1/num2;
        }
        String showResult = Double.toString(result);
        txtNumber.setText(showResult);
    }//GEN-LAST:event_btnIgualActionPerformed

